import React, { useState, useEffect } from 'react';
import './styles.css';

const FormContainer = ({ documentData, updateResultImage }) => {
  const [formData, setFormData] = useState({});

  useEffect(() => {
    if (documentData) {
      const initialFormData = {};
      documentData.steps.forEach(step => {
        step.fields.forEach(field => {
          initialFormData[field.input_name] = field.value || '';
        });
      });
      setFormData(initialFormData);
    }
  }, [documentData]);

  const handleInputChange = (e, inputName) => {
    setFormData({ ...formData, [inputName]: e.target.value });
  };

  const handleFileChange = (e, inputName) => {
    setFormData({ ...formData, [inputName]: e.target.files[0] });
  };

  const renderInputField = (field) => {
    if (field.type === 'file') {
      let placeholderImage;
      if (field.input_name === 'image1') {
        placeholderImage = 'https://oldie.veriftools.ru/media/generators/Finland%20passport/image1/PHOTODROP.jpg';
      } else if (field.input_name === 'image2') {
        placeholderImage = 'https://oldie.veriftools.ru/media/generators/Finland%20passport/image2/signature.jpg';
      }

      return (
        <div key={field.id} className="field-wrapper">
          <label>{field.input_label}</label>
          <img src={placeholderImage} alt={field.input_label} />
          <input 
            type="file" 
            onChange={(e) => handleFileChange(e, field.input_name)}
          />
        </div>
      );
    } else {
      return (
        <div key={field.id} className="field-wrapper">
          <label>{field.input_label}</label>
          <input 
            type={field.type} 
            value={formData[field.input_name]} 
            onChange={(e) => handleInputChange(e, field.input_name)}
            placeholder={field.input_placeholder}
          />
        </div>
      );
    }
  };
  const handleGenerateClick = async () => {
    setIsProcessing(true);
    const apiEndpoint = '/api/integration/generate/';
    const data = new FormData();

    for (const key in formData) {
      data.append(key, formData[key]);
    }

    try {
      const response = await fetch(apiEndpoint, {
        method: 'POST',
        body: data,
      });

      if (response.ok) {
        const result = await response.json();
        updateResultData(result.image_url); // Make sure to use updateResultData
      } else {
        console.error('Error processing image:', response.statusText);
      }
    } catch (error) {
      console.error('Error:', error);
    }
    setIsProcessing(false); // End processing
  };

  const renderFormFields = (fields) => {
    return fields.map(field => renderInputField(field));
  };

  return (
    <div className="form-container">
      {documentData && (
        <>
          <h2>{documentData.name}</h2>
          {documentData.steps.map(step => (
            <div key={step.id}>
              <h3>{step.name}</h3>
              {renderFormFields(step.fields)}
            </div>
          ))}
          <button onClick={handleGenerateClick} disabled={isProcessing}>Generate</button>
        {isProcessing && <p>Please allow 10-30 seconds for the result to show.</p>}
        </>
      )}
    </div>
  );
};

export default FormContainer;
