import React, { useState, useEffect } from 'react';
import Header from './Header';
import DocumentList from './DocumentList';
import FormContainer from './FormContainer';
import Result from './Result';
import './styles.css';

function App() {
  const [selectedDocument, setSelectedDocument] = useState(null);
  const [documentData, setDocumentData] = useState(null);
  const [resultData, setResultData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (selectedDocument) {
      const apiUrl = `https://old.verif.tools/api/integration/generator-full-information/${selectedDocument}`;
      fetch(apiUrl)
        .then(response => response.json())
        .then(data => {
          setDocumentData(data);
        })
        .catch(error => {
          console.error('Error fetching document data:', error);
        });
    }
  }, [selectedDocument]);

  const handleGenerate = (formData) => {
    setIsLoading(true);
    // Replace with actual API call to process formData
    fetch('your-api-endpoint', {
      method: 'POST',
      body: JSON.stringify(formData),
      headers: {
        'Content-Type': 'application/json'
        // Add any other headers as needed
      }
    })
    .then(response => response.json())
    .then(data => {
      setResultData(data);
      setIsLoading(false);
    })
    .catch(error => {
      console.error('Error generating document:', error);
      setIsLoading(false);
    });
  };

  return (
    <div>
      <Header />
      <div className="container">
        <DocumentList onDocumentSelect={setSelectedDocument} />
        <FormContainer documentData={documentData} onGenerate={handleGenerate} />
        <Result resultData={resultData} isLoading={isLoading} />
      </div>
    </div>
  );
}

export default App;
