import React, { useState, useEffect } from 'react';
import Header from './Header';
import DocumentList from './DocumentList';
import FormContainer from './FormContainer';
import Result from './Result';
import './styles.css';

function App() {
  const [selectedDocument, setSelectedDocument] = useState(null);
  const [documentData, setDocumentData] = useState(null);
  const [resultImage, setResultImage] = useState(null); // New state for the result image

  useEffect(() => {
    if (selectedDocument) {
      const apiUrl = `https://old.verif.tools/api/integration/generator-full-information/${selectedDocument}`;
      fetch(apiUrl)
        .then(response => response.json())
        .then(data => {
          setDocumentData(data);
          // Update the default image URL construction based on the new format
          const imageBaseUrl = 'https://oldie.veriftools.ru/media/generators/previews/';
          setResultImage(`${imageBaseUrl}${selectedDocument}_result.jpg`);
        })
        .catch(error => {
          console.error('Error fetching document data:', error);
        });
    } else {
      setDocumentData(null);
      setResultImage(null); // Reset the result image when no document is selected
    }
  }, [selectedDocument]);

  return (
    <div>
      <Header />
      <div className="container">
        <DocumentList onDocumentSelect={setSelectedDocument} />
        <FormContainer documentData={documentData} updateResultImage={setResultImage} />
        <Result resultImage={resultImage || defaultImage} />
      </div>
    </div>
  );
}

export default App;
