import React from 'react';
import './styles.css';

const FormContainer = ({ documentData }) => {
  // Function to render form fields based on the API response
  const renderFormFields = (fields) => {
    return fields.map((field, index) => (
      <div key={index}>
        <label>{field.input_label}</label>
        <input 
          type={field.type} 
          name={field.input_name} 
          defaultValue={field.value} 
          required={field.required} 
        />
      </div>
    ));
  };

  // Default content when no document is selected
  const renderDefaultContent = () => {
    return <div>Select a document to view its form</div>;
  };

  return (
    <div className="form-container">
      {documentData ? (
        <>
          <h2>{documentData.name}</h2>
          {documentData.steps && documentData.steps.map(step => (
            <div key={step.id}>
              <h3>{step.name}</h3>
              {renderFormFields(step.fields)}
            </div>
          ))}
        </>
      ) : (
        renderDefaultContent()
      )}
    </div>
  );
};

export default FormContainer;
