import React, { useState } from 'react';
import './styles.css';

const DocumentList = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);

  const documentCategories = {
    'Passports': ['finland_passport', 'russia_passport', 'malaysia_passport_2', /* other slugs */],
    'Driver Licenses': ['dl_ri', 'dl_texas', 'dl_alberta', /* other slugs */],
    // ... other categories
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category === selectedCategory ? null : category);
  };

  const handleDocumentClick = (slug) => {
    // Implement logic to update the Tabs component with the selected document's details/form
    // This could involve lifting state up to the App component or using a global state manager
  };

  return (
    <div className="column" id="document-list">
      <h2>List of documents</h2>
      {Object.keys(documentCategories).map(category => (
        <div key={category}>
          <button onClick={() => handleCategoryClick(category)}>{category}</button>
          {selectedCategory === category && (
            <ul>
              {documentCategories[category].map(slug => (
                <li key={slug} onClick={() => handleDocumentClick(slug)}>{slug}</li>
              ))}
            </ul>
          )}
        </div>
      ))}
    </div>
  );
};

export default DocumentList;
