import React, { useState, useEffect } from 'react';
import Header from './Header';
import DocumentList from './DocumentList';
import FormContainer from './FormContainer';
import Result from './Result';
import './styles.css';

function App() {
  const [selectedDocument, setSelectedDocument] = useState(null);
  const [documentData, setDocumentData] = useState(null);
  const [defaultImage, setDefaultImage] = useState(null);

  useEffect(() => {
    if (selectedDocument) {
      const apiUrl = `https://old.verif.tools/api/integration/generator-full-information/${selectedDocument}`;
      fetch(apiUrl)
        .then(response => response.json())
        .then(data => {
          setDocumentData(data);
          // Assuming 'image1_default' or similar key holds the default image URL
          setDefaultImage(data.image1_default || data.preview || 'default-placeholder-image-url');
        })
        .catch(error => {
          console.error('Error fetching document data:', error);
        });
    } else {
      setDocumentData(null);
      setDefaultImage(null); // Reset the default image when no document is selected
    }
  }, [selectedDocument]);

  return (
    <div>
      <Header />
      <div className="container">
        <DocumentList onDocumentSelect={setSelectedDocument} />
        <FormContainer documentData={documentData} />
        <Result resultImage={defaultImage} />
      </div>
    </div>
  );
}

export default App;
