import React, { useState, useEffect } from 'react';
import Header from './Header';
import DocumentList from './DocumentList';
import FormContainer from './FormContainer';
import Result from './Result';
import './styles.css';

function App() {
  const [selectedDocument, setSelectedDocument] = useState(null);
  const [documentData, setDocumentData] = useState(null);
  const [resultData, setResultData] = useState(null); // Changed to resultData

  useEffect(() => {
    if (selectedDocument) {
      const apiUrl = `https://old.verif.tools/api/integration/generator-full-information/${selectedDocument}`;
      fetch(apiUrl)
        .then(response => response.json())
        .then(data => {
          setDocumentData(data);
          setResultData(null); // Clear previous result when a new document is selected
        })
        .catch(error => {
          console.error('Error fetching document data:', error);
          setResultData("Error fetching document data"); // Show error message in result
        });
    } else {
      setDocumentData(null);
      setResultData(null); // Clear the result data when no document is selected
    }
  }, [selectedDocument]);

  const updateResultData = (newData) => {
    setResultData(newData);
  };

  return (
    <div>
      <Header />
      <div className="container">
        <DocumentList onDocumentSelect={setSelectedDocument} />
        <FormContainer documentData={documentData} updateResultData={updateResultData} />
        <Result resultData={resultData} />
      </div>
    </div>
  );
}

export default App;
